import 'package:flutter/material.dart';\nimport 'package:provider/provider.dart';\nimport '../../providers/data_provider.dart';\n\nclass DashboardScreen extends StatelessWidget {\n  const DashboardScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    final theme = Theme.of(context);\n    final dataProvider = Provider.of<DataProvider>(context);\n    \n    return Scaffold(\n      backgroundColor: theme.scaffoldBackgroundColor,\n      body: SingleChildScrollView(\n        padding: const EdgeInsets.all(24),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Welcome Section\n            Text(\n              'Welcome to Artist Pro',\n              style: theme.textTheme.headlineMedium?.copyWith(\n                fontWeight: FontWeight.bold,\n              ),\n            ),\n            const SizedBox(height: 8),\n            Text(\n              'Manage your music, shows, and finances all in one place',\n              style: theme.textTheme.bodyLarge?.copyWith(\n                color: theme.textTheme.bodySmall?.color?.withOpacity(0.7),\n              ),\n            ),\n            const SizedBox(height: 32),\n            // Stats Cards\n            LayoutBuilder(\n              builder: (context, constraints) {\n                final crossAxisCount = constraints.maxWidth > 1200\n                    ? 4\n                    : constraints.maxWidth > 768\n                        ? 2\n                        : 1;\n                return GridView.count(\n                  shrinkWrap: true,\n                  physics: const NeverScrollableScrollPhysics(),\n                  crossAxisCount: crossAxisCount,\n                  mainAxisSpacing: 16,\n                  crossAxisSpacing: 16,\n                  childAspectRatio: 1.8,\n                  children: [\n                    _buildStatCard(\n                      context,\n                      icon: Icons.library_music_rounded,\n                      title: 'Songs',\n                      value: '${dataProvider.songs.length}',\n                      color: Colors.blue,\n                    ),\n                    _buildStatCard(\n                      context,\n                      icon: Icons.queue_music_rounded,\n                      title: 'Setlists',\n                      value: '${dataProvider.setlists.length}',\n                      color: Colors.purple,\n                    ),\n                    _buildStatCard(\n                      context,\n                      icon: Icons.event_rounded,\n                      title: 'Events',\n                      value: '${dataProvider.events.length}',\n                      color: Colors.orange,\n                    ),\n                    _buildStatCard(\n                      context,\n                      icon: Icons.attach_money_rounded,\n                      title: 'Balance',\n                      value: '\$${dataProvider.balance.toStringAsFixed(2)}',\n                      color: Colors.green,\n                    ),\n                  ],\n                );\n              },\n            ),\n            const SizedBox(height: 32),\n            // Quick Actions\n            Text(\n              'Quick Actions',\n              style: theme.textTheme.titleLarge?.copyWith(\n                fontWeight: FontWeight.bold,\n              ),\n            ),\n            const SizedBox(height: 16),\n            Wrap(\n              spacing: 16,\n              runSpacing: 16,\n              children: [\n                _buildQuickActionCard(\n                  context,\n                  icon: Icons.add_rounded,\n                  title: 'Add Song',\n                  onTap: () {\n                    // TODO: Navigate to add song\n                  },\n                ),\n                _buildQuickActionCard(\n                  context,\n                  icon: Icons.playlist_add_rounded,\n                  title: 'Create Setlist',\n                  onTap: () {\n                    // TODO: Navigate to create setlist\n                  },\n                ),\n                _buildQuickActionCard(\n                  context,\n                  icon: Icons.event_rounded,\n                  title: 'Schedule Event',\n                  onTap: () {\n                    // TODO: Navigate to schedule event\n                  },\n                ),\n                _buildQuickActionCard(\n                  context,\n                  icon: Icons.monetization_on_rounded,\n                  title: 'Add Transaction',\n                  onTap: () {\n                    // TODO: Navigate to add transaction\n                  },\n                ),\n              ],\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildStatCard(\n    BuildContext context, {\n    required IconData icon,\n    required String title,\n    required String value,\n    required Color color,\n  }) {\n    final theme = Theme.of(context);\n    return Container(\n      padding: const EdgeInsets.all(24),\n      decoration: BoxDecoration(\n        color: theme.cardColor,\n        borderRadius: BorderRadius.circular(24),\n        boxShadow: [\n          BoxShadow(\n            color: Colors.black.withOpacity(0.05),\n            blurRadius: 10,\n            offset: const Offset(0, 4),\n          ),\n        ],\n      ),\n      child: Column(\n        crossAxisAlignment: CrossAxisAlignment.start,\n        mainAxisAlignment: MainAxisAlignment.spaceBetween,\n        children: [\n          Container(\n            padding: const EdgeInsets.all(12),\n            decoration: BoxDecoration(\n              color: color.withOpacity(0.1),\n              borderRadius: BorderRadius.circular(12),\n            ),\n            child: Icon(icon, color: color, size: 28),\n          ),\n          Column(\n            crossAxisAlignment: CrossAxisAlignment.start,\n            children: [\n              Text(\n                value,\n                style: theme.textTheme.headlineMedium?.copyWith(\n                  fontWeight: FontWeight.bold,\n                ),\n              ),\n              Text(\n                title,\n                style: theme.textTheme.bodyMedium?.copyWith(\n                  color: theme.textTheme.bodySmall?.color?.withOpacity(0.7),\n                ),\n              ),\n            ],\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildQuickActionCard(\n    BuildContext context, {\n    required IconData icon,\n    required String title,\n    required VoidCallback onTap,\n  }) {\n    final theme = Theme.of(context);\n    return InkWell(\n      onTap: onTap,\n      borderRadius: BorderRadius.circular(16),\n      child: Container(\n        width: 160,\n        padding: const EdgeInsets.all(20),\n        decoration: BoxDecoration(\n          color: theme.cardColor,\n          borderRadius: BorderRadius.circular(16),\n          border: Border.all(\n            color: theme.dividerColor.withOpacity(0.1),\n          ),\n        ),\n        child: Column(\n          children: [\n            Icon(icon, size: 32, color: theme.colorScheme.primary),\n            const SizedBox(height: 12),\n            Text(\n              title,\n              style: theme.textTheme.bodyMedium?.copyWith(\n                fontWeight: FontWeight.w600,\n              ),\n              textAlign: TextAlign.center,\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n